using System;
using System.Collections.Generic;

namespace tp09.Models
{
    public static class Pizzeria
    {
        private static List<Pizza> _ListaPizzas = new List<Pizza>();
        
        private static List<Ingrediente> _ListaIngredientes = new List<Ingrediente>(){
                                                                new Ingrediente("Salsa", "data:image/jpeg;base64,"),
                                                                new Ingrediente("Muzarella", "data:image/jpeg;base64,"),
                                                                new Ingrediente("Jamón", "data:image/jpeg;base64,"),
                                                                new Ingrediente("Tomates", "data:image/jpeg;base64,"),
                                                                new Ingrediente("Aceitunas", "data:image/jpeg;base64,"),
                                                                new Ingrediente("Rúcula", "data:image/jpeg;base64,"),
                                                                new Ingrediente("Cherrys", "data:image/jpeg;base64,"),
                                                                new Ingrediente("Cebolla", "data:image/jpeg;base64,"),
                                                                new Ingrediente("Oregano", "data:image/jpeg;base64,")
                                                            };

        public static List <Pizza> ListarPizzas()
        {
            return _ListaPizzas;
        }

        public static List <Ingrediente> ListarIngredientes()
        {
            return _ListaIngredientes;
        }

        public static bool AgregarPizza(Pizza Pizza)
        {
            _ListaPizzas.Add(Pizza);

            return true;
        }

        public static bool EliminarPizza(int id)
        {
            if(!_ListaPizzas.Exists(x => x.Id == id))
                return false;

            _ListaPizzas.RemoveAll(x => x.Id == id);
            return true;
        }
    }
}
